(self.webpackChunk_taiga_ui_components=self.webpackChunk_taiga_ui_components||[]).push([[85890],{85890:n=>{n.exports='<div\n    *ngFor="let platform of platforms; let first = first"\n    class="wrapper"\n    [class.wrapper_web]="platform === \'web\'"\n    [tuiPlatform]="platform"\n>\n    \x3c!--\n        This allows platform provider time to initialize.\n        Unnecessary for static platform tuiPlatform="whatever"\n    --\x3e\n    <ng-container *ngIf="getSize(first) as size">\n        <input\n            tuiCheckbox\n            type="checkbox"\n            [ngModel]="true"\n            [size]="size"\n        />\n\n        <input\n            tuiCheckbox\n            type="checkbox"\n            [indeterminate]="true"\n            [size]="size"\n        />\n\n        <input\n            tuiCheckbox\n            type="checkbox"\n            [disabled]="true"\n            [ngModel]="true"\n            [size]="size"\n        />\n\n        <input\n            tuiCheckbox\n            type="checkbox"\n            [size]="size"\n        />\n\n        <input\n            tuiCheckbox\n            type="checkbox"\n            [disabled]="true"\n            [ngModel]="false"\n            [size]="size"\n        />\n\n        <input\n            tuiCheckbox\n            type="checkbox"\n            [formControl]="invalidTrue"\n            [size]="size"\n        />\n\n        <input\n            tuiCheckbox\n            type="checkbox"\n            [formControl]="invalidFalse"\n            [size]="size"\n        />\n    </ng-container>\n</div>\n'}}]);